import React from 'react';
import { Form, Input, Button, Card, Avatar } from 'antd';
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';

const dummy = {
  isLoggedIn: true,
  imagePaths: [],
  mainPosts: [{
    User: {
      id: 1,
      nickname: '제로초',
    },
    content: '첫 게시글',
    img: 'https://image.yes24.com/Goods/62597864/XL',
  }],
};

const Home = () => {
  return (
    <div>
      {/* 글 쓰기 */}
      {dummy.isLoggedIn && <Form style={{ marginBottom: 20 }} encType="multipart/form-data">
        <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 있었나요?" />
        <div>
          <input type="file" multiple hidden />
          <Button>이미지 업로드</Button>
          <Button type="primary" style={{ float: 'right' }} htmlType="submit">짹짹</Button>
        </div>  

        {/* 업로드할 이미지 미리보기 */}
        <div>
          {dummy.imagePaths.map((v, i) => {
            return (
              <div key={v} style={{ display: 'inline-block' }}>
                <img src={'http://localhost:3065/' + v} style={{ width: '200px' }} alt={v} /> 
                <div>
                  <Button>제거</Button>
                </div>
              </div>
            )
          })}
        </div>
      </Form>}

      {/* 게시글 보기 */}
      {dummy.mainPosts.map((c) => {
        return (
          <Card
            key={+c.createdAt}
            cover={c.img && <img alt="example" src={c.img} style={{ height:'50%' }} />}
            actions={[
              <RetweetOutlined />,
              <HeartOutlined />,
              <MessageOutlined />,
              <EllipsisOutlined />,
            ]}
            extra={<Button>팔로우</Button>}
          >
            <Card.Meta 
              avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
              title={c.User.nickname}
              description={c.content}
            />
          </Card>
        )
      })}

    </div>
  );
};

export default Home;
