import React from 'react';
import { Form, Input, Button, Card, Avatar } from 'antd';
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';

const dummy = {
  isLoggedIn: true,
  imagePaths: [],
  mainPosts: [{
    User: {
      id: 1,
      nickname: '제로초',
    },
    content: '첫 게시글',
    img: 'https://image.yes24.com/Goods/62597864/XL',
  }],
};

const Home = () => {
  return (
    <div>
      {/* 글 쓰기 */}
      {dummy.isLoggedIn && <PostForm />}

      {/* 게시글 보기 */}
      {dummy.mainPosts.map((c) => {
        return (
          <Card
            key={+c.createdAt}
            cover={c.img && <img alt="example" src={c.img} style={{ }} />}
            actions={[
              <RetweetOutlined />,
              <HeartOutlined />,
              <MessageOutlined />,
              <EllipsisOutlined />,
            ]}
            extra={<Button>팔로우</Button>}
          >
            <Card.Meta 
              avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
              title={c.User.nickname}
              description={c.content}
            />
          </Card>
        )
      })}

    </div>
  );
};

export default Home;
